import React, { useState } from "react";
import { Button, Form } from "semantic-ui-react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import * as yup from "yup";

const Create = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [contact, setContact] = useState("");
  const navigate = useNavigate();

  let schema = yup.object().shape({
    firstName: yup.string().required(),
    lastName: yup.string().required(),
    email: yup.string().email(),
    contact: yup.number().required().positive().integer(),
  });
  const postData = () => {
    const emp_id = Math.floor(Math.random() * (99999 - 10000)) + 10000;

    const dataToInsert = {
      firstName,
      lastName,
      email,
      contact,
    };
    schema.isValid(dataToInsert).then(
      axios
        .post(`http://localhost:4000/create`, {
          emp_id,
          firstName,
          lastName,
          email,
          contact,
        })
        .then(() => {
          navigate("/");
        })
    );
  };
  const backHandler = () => {
    navigate("/");
  };
  return (
    <div>
      <Button primary onClick={backHandler}>
        Back
      </Button>
      <Form className="create-form">
        <Form.Field>
          <label>First Name</label>
          <input
            placeholder="First Name"
            onChange={(e) => setFirstName(e.target.value)}
          />
        </Form.Field>
        <Form.Field>
          <label>Last Name</label>
          <input
            placeholder="Last Name"
            onChange={(e) => setLastName(e.target.value)}
          />
        </Form.Field>
        <Form.Field>
          <label>Email</label>
          <input
            placeholder="Email"
            onChange={(e) => setEmail(e.target.value)}
          />
        </Form.Field>
        <Form.Field>
          <label>Contact</label>
          <input
            placeholder="Contact"
            onChange={(e) => setContact(e.target.value)}
          />
        </Form.Field>
        <Button color="green" onClick={postData} type="submit">
          Submit
        </Button>
      </Form>
    </div>
  );
};

export default Create;
