import React, { useState } from "react";
import { Button, Form } from "semantic-ui-react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";

const Create = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [contact, setContact] = useState("");
  const navigate = useNavigate();

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const postData = () => {
    const emp_id = Math.floor(Math.random() * (99999 - 10000)) + 10000;
    if (
      firstName == "" ||
      lastName == "" ||
      email == "" ||
      contact.length < 10
    ) {
    } else {
      axios
        .post(`http://localhost:4000/create`, {
          emp_id,
          firstName,
          lastName,
          email,
          contact,
        })
        .then(() => {
          navigate("/");
        });
    }
  };
  const onSubmit = (data) => {
    console.log(errors);
    console.log(data);
  };
  const backHandler = () => {
    navigate("/");
  };
  return (
    <div>
      <Button primary onClick={backHandler}>
        Back
      </Button>
      <Form className="create-form">
        <Form.Field>
          <label>First Name</label>
          <input
            placeholder="First Name"
            onChange={(e) => setFirstName(e.target.value)}
            type="text"
            {...register("firstName", { required: true, maxLength: 10 })}
          />
        </Form.Field>
        {errors.firstName && <p>Please check the First Name</p>}

        <Form.Field>
          <label>Last Name</label>
          <input
            placeholder="Last Name"
            onChange={(e) => setLastName(e.target.value)}
            type="text"
            {...register("lastName", { required: true, maxLength: 10 })}
          />
        </Form.Field>
        {errors.lastName && <p>Please check the Last Name</p>}
        <Form.Field>
          <label>Email</label>
          <input
            placeholder="Email"
            onChange={(e) => setEmail(e.target.value)}
            type="email"
            {...register("email", { required: true })}
          />
        </Form.Field>
        {errors.email && <p>Please check the Email</p>}

        <Form.Field>
          <label>Contact</label>
          <input
            placeholder="Contact"
            onChange={(e) => setContact(e.target.value)}
            type="number"
            {...register("contact", { required: true })}
          />
        </Form.Field>
        {errors.contact && <p>Contact must be of 10 characters</p>}

        <Button color="green" type="submit" onClick={postData}>
          Submit
        </Button>
      </Form>
    </div>
  );
};

export default Create;
